import React, { useEffect, useState } from "react";

import Web3 from "web3";
// import styled from "styled-components";

import HelloWorldABI from "./ABI/HelloWorld.json";

// const StakingContract = "0x6cFf0b2d01B1e7892f9b66A2D7A39777464aBFc5";

function HelloWorld() {
  const [walletAmount, setWalletAmount] = useState(0);

  async function getWalletAmount() {
    async function loadWeb3() {
      if (window.ethereum) {
        window.web3 = new Web3(window.ethereum);
        await window.ethereum.enable();
        return true;
      } else if (window.web3) {
        window.web3 = new Web3(window.web3.currentProvider);
        return true;
      } else {
        return false;
      }
    }

    var wallet = await loadWeb3();

    if (wallet) {
      const web3 = window.web3;

      const accounts = await web3.eth.getAccounts();
      const address = { account: accounts[0] }.account;

      if (address) {
        web3.eth.getBalance(address, function (error, wei) {
          if (!error) {
            var balance = web3.utils.fromWei(wei, "ether");
            setWalletAmount(balance.substring(0, 4));
          }
        });
      }
    }
  }

  useEffect(() => {
    getWalletAmount();
  }, []);

  return (
    <>
      <h2 Style="text-align: center"> Hello World </h2>
      <h2 Style="text-align: center"> Total Amount: {walletAmount} </h2>
    </>
  );
}

export default HelloWorld;
